	(134, '#!/bin/bash\n\n# Имя очереди сообщений\nMESSAGE_QUEUE="/my_message_queue"\n\n# Создаем очередь сообщений\nif [ ! -e $MESSAGE_QUEUE ]; then\n    echo "Creating message queue..."\n    mkfifo $MESSAGE_QUEUE\nfi\n\n# Функция для отправки сообщения\nsend_message() {\n    local message=$1\n    echo $message > $MESSAGE_QUEUE\n    echo "Sent message: $message"\n}\n\n# Функция для чтения сообщения\nreceive_message() {\n    local message=$(cat $MESSAGE_QUEUE)\n    echo "Received message: $message"\n}\n\n# Процесс-отправитель\nproducer() {\n    for i in {1..5}; do\n        send_message "Message $i from producer"\n        sleep 1\n    done\n}\n\n# Процесс-получатель\nconsumer() {\n    for i in {1..5}; do\n        receive_message\n        sleep 1\n    done\n}\n\n# Запускаем процессы\nproducer &\nproducer_pid=$!\n\nconsumer &\nconsumer_pid=$!\n\n# Ожидаем завершения процессов\nwait $producer_pid\nwait $consumer_pid\n\n# Удаляем очередь сообщений\nrm $MESSAGE_QUEUE\necho "Message queue removed."\n', '#!/bin/bash', 4, '2023-12-05 18:29:52', 'bin-bash', ' 1 5 bash bin cat chteniya consumer creating dlya do done e echo fi for from funkciya i if imya in local message mkfifo my ochered ocheredi ojidaem otpravitel otpravki pid poluchatel process processov processy producer queue receive received removed rm send sent sleep soobsheniy soobsheniya sozdaem then udalyaem wait zapuskaem zaversheniya', 0, '0000-00-00 00:00:00', 0, 0),
